/*
 * Breakpoints.js
 * Responsive breakpoints utility
 */
(function() {
    var breakpoints = {
        xlarge: [ '1281px', '1680px' ],
        large: [ '981px', '1280px' ],
        medium: [ '737px', '980px' ],
        small: [ '481px', '736px' ],
        xsmall: [ null, '480px' ]
    };

    window.breakpoints = {
        active: function(name) {
            var bp = breakpoints[name];
            if (!bp) return false;
            
            var width = window.innerWidth;
            var min = bp[0] ? parseInt(bp[0]) : 0;
            var max = bp[1] ? parseInt(bp[1]) : Infinity;
            
            return width >= min && width <= max;
        }
    };
})();

